public class 제어문_반목문_6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (char c='A'; c<='Z';c++) {
			System.out.print(c+" ");
		}
		System.out.println();					//출력 용도, 다음 줄 출력.
		for (char d='a'; d<='z'; d++) {
			System.out.print(d+" ");
		}
		System.out.println();
		for (char c='Z'; c>='A';c--) {
			System.out.print(c+" ");
			}
	}
}
