package 실습문제;

public class 연산자문제_1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a;   
		   int b;   
		   a=3;   
		   b=a+5;       
		   System.out.printf("a의 값: %d \n", a);   
		   /*
		    *  %d : 정수값 첨부
		    *  %f : 실수값 첨부
		    *  %c : 문자 첨부
		    *  %s : 문자열 첨부
		    *  출력형식을 만들어서 처리->printf->c언어 출력형식 도입
		    */
		   
		   System.out.printf("b의 값: %d \n", b);   

	}

}
