package 실습문제;

public class 연산자문제_11 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	    int i = 0;
	    int re = 0;
	    i=3;
	    re = ++i;
	    System.out.println("re:"+re + ", i:"+i);
	    i = 3;
	    re = i++;
	    System.out.println("re:"+re + ", i:"+i); 
	}

}
