package 실습문제;

public class 연산자문제_8 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	    int num1=10, num2=10;
	    System.out.printf("%d \n", ++num1);
	    System.out.printf("%d \n", num1);
	    System.out.printf("%d \n", num2++);
	    System.out.printf("%d \n", num2);

	}

}
